mapper.select:
			select count(1) from tbl_product_info t

				 	t.PRODUCT_TYPE = 0

                    and t.brand_id=#{brand_id,jdbcType=INTEGER}


                    and t.year=#{year,jdbcType=INTEGER}


                    and t.season_id=#{season_id,jdbcType=INTEGER}


                    and t.itemnumber like '%'||#{itemnumber}||'%'


					and	(t.product_type_id = #{product_type_id,jdbcType=INTEGER}
					or t.product_type_id in (select id from tbl_dic_product_type start with parent_id = #{product_type_id,jdbcType=INTEGER} connect by prior id=parent_id))

			select count(1) from tbl_pvtp_product_info t

				 	1=1

                    and t.brand_id=#{brand_id,jdbcType=INTEGER}


                    and t.year=#{year,jdbcType=INTEGER}


                    and t.season_id=#{season_id,jdbcType=INTEGER}


                    and t.itemnumber like '%'||#{itemnumber}||'%'
