SELECT A.*, (SELECT TOI.PAYMENT_MONEY FROM TBL_ORDER_INFO TOI WHERE TOI.ORDER_NUMBER = A.ORDER_NUMBER ) AS PAYMENT_MONEY, (SELECT LOGIN_NAME FROM TBL_USER_INFO WHERE USER_NAME = A.USER_NAME) AS LOGIN_NAME, (CASE A.APPLY_TYPE WHEN '1' THEN (SELECT LOGIN_NAME FROM TBL_USER_INFO T WHERE T.ID=A.APPLY_USER_NAME) WHEN '2' THEN APPLY_USER_NAME END) AS APPLY_USER_NAME_R, (SELECT COMPANY_NAME FROM TBL_STATIONED_USER_INFO WHERE ID = A.STATIONED_USER_ID) STATIONED_USER_NAME, (SELECT CONTACTS FROM TBL_STATIONED_USER_INFO TSUI WHERE TSUI.USER_NAME=(SELECT YWJL_USER_NAME FROM TBL_ORDER_INFO T WHERE T.ORDER_NUMBER=A.ORDER_NUMBER)) AS STATIONED_YWJL_USER_REALNAME, (SELECT CONTACTS FROM TBL_STATIONED_USER_INFO TSUI WHERE TSUI.USER_NAME=(SELECT YWY_USER_NAME FROM TBL_ORDER_INFO T WHERE T.ORDER_NUMBER=A.ORDER_NUMBER)) AS STATIONED_YWY_USER_NAME, (SELECT STORE_NAME FROM TBL_STORE_INFO TSI WHERE TSI.ID=(SELECT MD_ID FROM TBL_ORDER_INFO T WHERE T.ORDER_NUMBER=A.ORDER_NUMBER)) AS STATIONED_STORE_NAME FROM (SELECT T.*,ROWNUM AS RN FROM (SELECT FROM TBL_ORDER_RETURN_INFO TORI
TORI.ORDER_TYPE = 2 AND EXISTS( SELECT 1 FROM TBL_ORDER_INFO TOI
1=1 AND TOI.ORDER_NUMBER = TORI.ORDER_NUMBER
AND EXISTS(SELECT 1 FROM TBL_STATIONED_USER_INFO TSUI WHERE TSUI.USER_NAME=TOI.YWJL_USER_NAME AND TSUI.ID=#{ywjl_user_id,jdbcType=INTEGER})
AND EXISTS(SELECT 1 FROM TBL_STORE_INFO TSI WHERE TSI.ID=TOI.MD_ID AND TSI.ID=#{md_id,jdbcType=INTEGER})
AND EXISTS(SELECT 1 FROM TBL_STATIONED_USER_INFO TSUI WHERE TSUI.USER_NAME=TOI.YWY_USER_NAME AND TSUI.ID=#{ywy_user_id,jdbcType=INTEGER})
)
AND TORI.STATE IN
#{item}
AND TORI.ORDER_NUMBER = #{order_number,jdbcType=VARCHAR}
AND TORI.RETURN_NUMBER = #{return_number,jdbcType=VARCHAR}
AND TORI.APPLY_TYPE = #{apply_type,jdbcType=CHAR}
AND TORI.CREATE_DATE >= to_date(#{start_date,jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')
AND TORI.CREATE_DATE <= to_date(#{end_date,jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')
AND EXISTS(SELECT 1 FROM TBL_USER_INFO TUI WHERE TUI.USER_NAME = TORI.USER_NAME AND TUI.LOGIN_NAME = #{login_name,jdbcType=VARCHAR})
AND TORI.STATIONED_USER_ID = #{stationed_id,jdbcType=INTEGER}
ORDER BY TORI.CREATE_DATE DESC) T WHERE ROWNUM <= #{end_rownum,jdbcType=INTEGER}) A WHERE RN > #{start_rownum,jdbcType=INTEGER}

