mapper.select:
select
    count(dt.id)
from
    tbl_decorate_template dt
where
    dt.template_name = #{template_name,jdbcType=VARCHAR}
    and dt.site_id = #{site_id,jdbcType=INTEGER}

	and id != #{id,jdbcType=INTEGER}

select t.*,
	case when effect_start_time > sysdate then 1
		 when effect_start_time <= sysdate and effect_end_time >= sysdate then 2
		 when effect_end_time < sysdate then 3
		 else 0
	end effect_state,
	to_char(effect_start_time, 'YYYY-MM-DD HH24:MI:SS') as effect_start_time,
	to_char(effect_end_time, 'YYYY-MM-DD HH24:MI:SS') as effect_end_time,
	to_char(create_date, 'YYYY-MM-DD HH24:MI:SS') as create_date
 from (
		select a.*,rownum rn from (
			select id,
				template_name,
				enable_state,
				effect_start_time,
				effect_end_time,
				create_date
			from tbl_app_icon_template_config
		) a where rownum <= #{end_rownum,jdbcType=INTEGER}
) t where t.rn > #{start_rownum,jdbcType=INTEGER}

mapper.update:
update tbl_app_icon_template_config set
	enable_state = #{enable_state, jdbcType=INTEGER},
	update_date = sysdate,
	update_id = #{public_user_id, jdbcType=INTEGER}
where id = #{id, jdbcType=INTEGER}
