and exists (select 1 from tbl_user_info where id = tusa.user_id and market_supervision_user_id = #{public_user_id,jdbcType=INTEGER}) and exists (select 1 from tbl_agent_control_area_conf where city_id = tusa.city and user_id = #{public_user_id,jdbcType=INTEGER})
and exists (select 1 from tbl_user_info where id = tusa.user_id and referee_user_id in (select user_id from tbl_store_user_rel where type = '3' and store_id in (select id from tbl_store_info where state = '2' and shopkeeper_user_id = #{public_user_id,jdbcType=INTEGER}))) and exists (select 1 from tbl_store_control_area_conf where city_id = tusa.city and store in (select id from tbl_store_info where shopkeeper_user_id = #{public_user_id,jdbcType=INTEGER}))
and tusa.state = #{state,jdbcType=CHAR}
and tpc.product_control_type = #{product_control_type,jdbcType=CHAR}
and tusa.user_store_state in
#{item,jdbcType=INTEGER}
and (
exists (select 1 from tbl_product_control t,tbl_product_control_detail t1 where t.id = t1.product_control_id and t1.product_control_mode in
#{item,jdbcType=CHAR}
and t.user_store_address_id = tusa.id)
or
