mapper.select:
select (select brand_name from tbl_dic_product_brand where id = product_control_brand) brand_name,
	   case when product_control_mode = '1' then '全部控货'
	   		else '下单控货' end product_control_modes,
	   logo
  from tbl_product_control_detail
 where product_control_id = #{id,jdbcType=INTEGER}

select product_control_id,(select brand_name from tbl_dic_product_brand where id = product_control_brand) brand_name, case when product_control_mode = '1' then '全部控货' else '下单控货' end product_control_modes, logo from tbl_product_control_detail where product_control_id in
#{item}
order by product_control_id

select id
  from tbl_product_control_apply 
 where user_store_address_id = #{user_store_address_id,jdbcType=INTEGER}

  	select count(1) 
		  from tbl_product_control_apply 
		 where id = #{id,jdbcType=INTEGER}

		   and approval_state = #{approval_state,jdbcType=CHAR}

mapper.update:
	update tbl_product_control_apply
	   set approval_date = sysdate,
           approval_user_id = #{public_user_id,jdbcType=INTEGER},

           rejected_reason = #{rejected_reason,jdbcType=VARCHAR},

           approval_state = #{approval_state,jdbcType=CHAR}
     where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT t2.id, t1.state, t1.surrounding_USER_ID as USER_ID, (select user_state from tbl_user_info where id=t1.surrounding_USER_ID) as user_state, (select login_name from tbl_user_info where id = t1.surrounding_USER_ID) login_name, t1.longitude, t1.latitude, (select type_name from tbl_product_control_type where id = t2.product_control_type) type_name, nvl(t2.product_control_radius,0) product_control_radius, (select count(1) from tbl_product_control_detail where product_control_id = t2.id) total FROM (SELECT TUSA1.USER_ID AS USER_ID, TUSA1.ID AS STORE_ID, TUSA2.STATE, TUSA2.USER_ID AS surrounding_USER_ID, TUSA2.ID AS surrounding_STORE_ID, GETDISTANCE (TUSA1.LONGITUDE, TUSA1.LATITUDE, TUSA2.LONGITUDE, TUSA2.LATITUDE) AS DISTANCE, tusa2.longitude, tusa2.latitude FROM TBL_USER_STORE_ADDRESS TUSA1, TBL_USER_STORE_ADDRESS TUSA2 WHERE TUSA2.APPROVAL_STATE in (0,1) AND TUSA1.USER_STORE_STATE = 2 AND TUSA2.USER_STORE_STATE = 2 AND TUSA1.LONGITUDE IS NOT NULL AND TUSA1.LATITUDE IS NOT NULL AND TUSA2.LONGITUDE IS NOT NULL AND TUSA2.LATITUDE IS NOT NULL AND TUSA2.ID != #{user_store_address_id,jdbcType=INTEGER} AND TUSA1.ID = #{user_store_address_id,jdbcType=INTEGER}
and exists (select 1 from tbl_user_info where id = TUSA2.USER_ID and referee_user_id = #{public_user_id,jdbcType=INTEGER}) and exists (select 1 from tbl_agent_control_area_conf where city_id = TUSA2.city and user_id = #{public_user_id,jdbcType=INTEGER})
and exists (select 1 from tbl_user_info where id = TUSA2.USER_ID and market_supervision_user_id = #{public_user_id,jdbcType=INTEGER}) and exists (select 1 from tbl_agent_control_area_conf where city_id = TUSA2.city and user_id = #{public_user_id,jdbcType=INTEGER})
and exists (select 1 from tbl_store_user_rel where user_id = TUSA2.USER_ID and type = '3' and store_id in (select id from tbl_store_info where state = '2' and shopkeeper_user_id = #{public_user_id,jdbcType=INTEGER})) and exists (select 1 from tbl_store_control_area_conf where city_id = TUSA2.city and store in (select id from tbl_store_info where shopkeeper_user_id = #{public_user_id,jdbcType=INTEGER}))
) T1,tbl_product_control t2 WHERE T1.DISTANCE <= 15000 AND T1.surrounding_USER_ID = T2.USER_ID(+) AND T1.surrounding_STORE_ID = T2.USER_STORE_ADDRESS_ID(+)

select * from (select t.*,rownum rn from ( select t1.user_id, t2.login_name, t2.user_manage_name, t2.user_manage_mobilephone, t2.referee_user_realname, t2.market_supervision_user_realna, to_char(t2.create_date,'yyyy-mm-dd hh24:mi:ss') create_date, (select count(1) from tbl_user_store_address tusa,tbl_product_control_apply tpca where tusa.user_id = t1.user_id and tusa.approval_state in (0,1) and tusa.longitude is not null and tusa.latitude is not null and tusa.state = '2' and tusa.user_id = tpca.user_id and tusa.id = tpca.user_store_address_id and tpca.approval_state = 2) store_count, t.brand_name, case when t.duration_start_date is not null then t.duration_start_date||'~'||t.duration_end_date end frist_condition, case when t.target_type=1 then '月度采购金额>='||t.target_value||'元' when t.target_type=2 then '月度采购量>='||t.target_value||'件' end last_condition, (select to_char(min(approval_date),'yyyy-mm-dd hh24:mi:ss') from tbl_product_control_apply where user_id = t1.user_id) approval_date from tbl_user_control_set_detail t, tbl_user_control_set t1,tbl_user_info t2 where t.control_set_id = t1.id and t1.user_id = t2.id and t1.control_restrict = 2
union all select t1.user_id, t2.login_name, t2.user_manage_name, t2.user_manage_mobilephone, t2.referee_user_realname, t2.market_supervision_user_realna, to_char(t2.create_date,'yyyy-mm-dd hh24:mi:ss') create_date, (select count(1) from tbl_user_store_address tusa,tbl_product_control_apply tpca where tusa.user_id = t1.user_id and tusa.approval_state in (0,1) and tusa.longitude is not null and tusa.latitude is not null and tusa.state = '2' and tusa.user_id = tpca.user_id and tusa.id = tpca.user_store_address_id and tpca.approval_state = 2) store_count, '全部品牌' brand_name, (select case when t.duration_start_date is not null then t.duration_start_date||'~'||t.duration_end_date end from tbl_user_control_set_detail t where t.control_set_id = t1.id and rownum < 2) frist_condition, (select case when t.target_type=1 then '月度采购金额>='||t.target_value||'元' when t.target_type=2 then '月度采购量>='||t.target_value||'件' end from tbl_user_control_set_detail t where t.control_set_id = t1.id and rownum < 2) last_condition, (select to_char(min(approval_date),'yyyy-mm-dd hh24:mi:ss') from tbl_product_control_apply where user_id = t1.user_id) approval_date from tbl_user_control_set t1,tbl_user_info t2 where t1.user_id = t2.id and t1.control_restrict = 1
) t where rownum <= #{end_rownum,jdbcType=INTEGER} ) where rn > #{start_rownum,jdbcType=INTEGER}

select to_char(min(approval_date),'yyyy-mm-dd') approval_date,to_char(min(approval_date),'yyyy-mm') year_month
  from tbl_product_control_apply 
 where user_id = #{user_id,jdbcType=INTEGER}
